import React, { Component } from "react";
import CardModal from "./CardModal";

export class Card extends Component {
	constructor(props) {
		super(props);
		this.state = { openModal: flase };
	}

    openModal = () =>{
        this.setState({openModoal:true})
    }

	render() {
		const { name } = this.props.card;
		return (
			<div>
				<div onclick = {this.openModal} className="bg-white rounded w-11/12 p-1 mx-auto my-1">
					<p>{name}</p>
					<  CardModal />
					<div
						class=" bg-black bg-opacity-5 absolute inset-0 h-screen flex items-center justify-center "
						id="overlay"
					>
						<div className="bg-white flex items-start justify-around w-5/12 h-3/4 p-4">
							<div>
								<h1>Titile</h1>
								<p>Description</p>
								<p>Activity</p>
							</div>
							<div>
								<h1>Add to card</h1>
								<p>checklist</p>
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

export default Card;
