import React, { Component } from "react";
import Card from "./Card";

export class List extends Component {
	constructor(props) {
		super(props);
		// console.log(props.list.id);
		this.state = { cards: [] };
	}

	UpdateData = () => {
		fetch(
			`https://api.trello.com/1/lists/${this.props}/cards?key=a32c5c0c541016f7fd5c81bc1e4e47ef&token=a4711b0c6df1e11c11b241284521cf44441681fb61c66088f45fae8a9a4501f6`,
			{
				method: "GET",
			}
		)
			.then((response) => {
				return response.json();
			})
			.then((data) => {
				console.log(data);
				this.setState({ cards: data });

				console.log(this.state.cards);
			})
			.catch((err) => console.error(err));
	};
	componentDidMount() {
		this.UpdateData();
	}

	render() {
		const { name } = this.props.list;

		// let cards = this.state.cards.map((card) => {
		// 	return <Card cards={card} />;
		// });
		return (
			<div className="bg-gray-200 flex-shrink-0 flex-column w-60 m-5 px-2 pb-7 pt-3">
				<div>{name}</div>
				{/* {cards} */}
			</div>
		);
	}
}

export default List;
