import React, { Component } from "react";

import Navbar from "./Navbar";
import Workspace from "./Workspace";

import BoardList from "./BoardLists";

export class App extends Component {
	constructor(props) {
		super(props);
		this.state = { boardId: "", isClicked: false };
	}

	getBoardId = (id) => {
		this.setState({ boardId: id, isClicked: true });
	};
	UpdateData = () => {
		fetch(
			`https://api.trello.com/1/boards/${this.props.boardId}/lists?key=a32c5c0c541016f7fd5c81bc1e4e47ef&token=a4711b0c6df1e11c11b241284521cf44441681fb61c66088f45fae8a9a4501f6`,
			{
				method: "GET",
			}
		)
			.then((response) => {
				return response.json();
			})
			.then((data) => {
				this.setState({ lists: data, loading: false });
			})
			.catch((err) => console.error(err));
	};
