import React, { Component } from "react";
import List from "./List";

export class BoardLists extends Component {
	constructor(props) {
		super(props);

		this.state = { lists: [], loading: true };
	}
	UpdateData = () => {
		fetch(
			`https://api.trello.com/1/boards/${this.props.boardId}/lists?key=a32c5c0c541016f7fd5c81bc1e4e47ef&token=a4711b0c6df1e11c11b241284521cf44441681fb61c66088f45fae8a9a4501f6`,
			{
				method: "GET",
			}
		)
			.then((response) => {
				return response.json();
			})
			.then((data) => {
				this.setState({ lists: data, loading: false });
			})
			.then((data) => {
				fetch(
					"https://api.trello.com/1/cards?idList=5abbe4b7ddc1b351ef961414",
					{
						method: "POST",
						headers: {
							Accept: "application/json",
						},
					}
				)
					.then((response) => {
						console.log(`Response: ${response.status} ${response.statusText}`);
						return response.text();
					})
					.then((text) => console.log(text))
					.catch((err) => console.error(err));
			})

			.catch((err) => console.error(err));
	};

	componentDidMount = () => {
		this.UpdateData();
	};
	componentDidUpdate = () => {
		this.UpdateData();
	};

	render() {
		let lists = this.state.lists.map((list) => {
			return <List key={list.id} {...list} />;
		});
		if (!this.state.loading) return <div className="flex">{lists}</div>;
		else {
			return <div>Loading...........</div>;
		}
	}
}

export default BoardLists;
