import React, { Component } from "react";

export class CardModal extends Component {
	constructor(props) {
		super(props);
	}

	handleClose = () => {
		this.props.handleClose();
	};
	render() {
		return (
			<div
				className=" bg-black bg-opacity-5 absolute inset-0 h-screen flex items-center justify-center "
				id="overlay"
			>
				<div className="bg-white relative flex items-start justify-around w-5/12 h-3/4 p-4">
					<div>
						<h1>{this.props.name}</h1>
						<p>Description</p>
						<p>Activity</p>
					</div>
					<div>
						<h1>Add to card</h1>
						<p>checklist</p>
					</div>
					<button
						onClick={this.handleClose}
						className="absolute top-0 right-0 p-10"
					>
						x
					</button>
				</div>
			</div>
		);
	}
}

export default CardModal;
