import React, { Component } from "react";

import { Route, Switch } from "react-router";

import Navbar from "./Navbar";
import Workspace from "./Workspace";

import BoardList from "./BoardLists";

export class App extends Component {
	constructor(props) {
		super(props);
		this.state = { boardId: "", isClicked: false };
	}

	getBoardId = (id) => {
		this.setState({ boardId: id, isClicked: true });
	};

	componentDidMount = () => {};

	render() {
		return (
			<div>
				<Navbar />

				<Switch>
					<Route
						exact
						path="/"
						component={() => <Workspace handleClick={this.getBoardId} />}
					/>

					<Route
						exact
						path="/:id"
						component={(routeProps) => <BoardList  />}
						boardId={this.state.boardId}
					/>

					{/* {this.state.isClicked && <BoardList boardId={this.state.boardId} />} */}
				</Switch>
			</div>
		);
	}
}

export default App;
